import json
import random
import java.io
from org.apache.commons.io import IOUtils
from java.nio.charset import StandardCharsets
from org.apache.nifi.processor.io import StreamCallback

class PyStreamCallback(StreamCallback):
  def __init__(self,flowFile):
        self.flowFile=flowFile
        pass
  def process(self, inputStream, outputStream):
    text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    campaigns= json.loads(text)
    campaign = random.choice(campaigns['campaigns'])
    userid=self.flowFile.getAttribute("http.query.param.uid")
    if userid is None:
         userid=""
    html="""
    <div id="adserve1">
    <ul style="list-style: none;">
    <script type="text/javascript">
    function clicked(){
    var xmlhttp = new XMLHttpRequest();
    var randomh=Math.random();
    console.log("ad clicked");
    xmlhttp.open("GET","http://dzaratsian-10.field.hortonworks.com:7080/adserve?method=clicked&campaignid="""+campaign['campaignid']+"""&userid="""+userid+"""&"+randomh, true);
    xmlhttp.send();
    window.open(" """+campaign['Landing_Url']+""" ","_blank");
    }
    </script>
    <li>
    <img class="adimg" style="max-width:200px;" src=" http://"""+campaign['campaignurl'].replace('qwqng','qwang')+""" " onclick="clicked()"/>
    </li><script type="text/javascript">
    var xmlhttp = new XMLHttpRequest();
    randomh=Math.random();
    console.log("ad shown");
    xmlhttp.open("GET", "http://dzaratsian-10.field.hortonworks.com:7080/adserve?method=shown&campaignid="""+campaign['campaignid']+"""&userid="""+userid+"""&"+randomh, true);
    xmlhttp.send();
    </script>
    <li><div style="font-size:13px;">Checkout this new cool ad!</div></li>
    <li><div style="font-size:10px;">&nbsp;&nbsp;&nbsp;&nbsp;Sponsored Content</div></li>
    </ul>
    </div>"""
    outputStream.write(bytearray(html.encode('utf-8')))

flowFile = session.get()
if (flowFile != None):
  flowFile = session.write(flowFile,PyStreamCallback(flowFile))
  session.transfer(flowFile, REL_SUCCESS)

import json import java.io from org.apache.commons.io import IOUtils from java.nio.charset import StandardCharsets from org.apache.nifi.processor.io import StreamCallback
class PyStreamCallback(StreamCallback): def __init__(self): pass def process(self, inputStream, outputStream): text = IOUtils.toString(inputStream, StandardCharsets.UTF_8) text=text.replace('"}{"userid"','"},\n{"userid"') text='{"users":['+text+"]}" outputStream.write(bytearray(text.encode('utf-8')))
flowFile = session.get() if (flowFile != None): flowFile = session.write(flowFile,PyStreamCallback()) flowFile = session.putAttribute(flowFile, "filename", flowFile.getAttribute('filename').split('.')[0]+'_translated.json') session.transfer(flowFile, REL_SUCCESS)

import java.nio.charset.StandardCharsets

def flowFile = session.get()
if(!flowFile) return

flowFile = session.write(flowFile, {inputStream, outputStream ->
   inputStream.eachLine { line ->
      a = line.tokenize('|')

      a.remove(0);
      def finalString = a.join("|");

        outputStream.write("$finalString".toString().getBytes(StandardCharsets.UTF_8))
   }
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
